create table buildings(
              bid varchar(5),
              bname varchar(50),
              bloc MDSYS.SDO_GEOMETRY,
              primary key (bid)
);
create table hydrant(
              hid varchar(10),
              hloc MDSYS.SDO_GEOMETRY,
              primary key (hid)
);

create table firebuilding(
              fname varchar(50),
              primary key (fname)
);

